package com.codebaobao.filter;import com.codebaobao.utils.JwtTokenUtil;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.web.servlet.FilterRegistrationBean;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import javax.servlet.Filter;@Configurationpublic class FilterConfiguration {    @Bean    public FilterRegistrationBean filterRegistrationBean(JwtTokenUtil jwtTokenUtil) {        final FilterRegistrationBean registrationBean = new FilterRegistrationBean();        registrationBean.setFilter(new AuthenticationJailer(jwtTokenUtil));        registrationBean.addUrlPatterns("/*");        return registrationBean;    }}